(274, 'registration_email_info', 'Below email messages will be sent to users when they register on this site, depending upon the registration type selected under Plguin Configuration.\n					<br />You can use dynamic variables in your messages to personalize the email and populate different values. <a target="_blank" href="http://qlt-ecommerce.com:8080/wp-admin/admin.php?page=cjfm&callback=cjfm_customize_emails_variables">Click here</a> to view dynamic variable list.'),
(275, 'verify_email_heading', 'Email Verification Messages'),
(276, 'verify_email_subject', 'Comfirm your email address'),
(277, 'verify_email_address_message', '<p>Dear User,</p>\n<p>Thank you for creating an account on %%site_name%%.</p>\n<p>Please follow the link below to confirm your email address and activate your account.</p>\n<p><a href="%%verify_email_link%%">%%verify_email_link%%</a></p>\n<p>%%signature%%</p>'),
(278, 'verify_email_onscreen_message', 'We have sent an email with a confirmation link to your email address.<br>Please check your email (also spam folder) in order to activate your account.'),
(279, 'registration_welcome_email_info', 'Welcome email message'),
(280, 'welcome_email_subject', 'Your Qualitair account has been created.'),
(281, 'welcome_email_message', 'Dear %%display_name%%,\n<p>Congratulations!!! Your account has been successfully created on %%site_name%%.</p>\n<p>Please find the login details below:</p>\n<p>\nUsername: %%user_login%% <br />\nPassword: %%cjfm_rp%% <br />\nLogin URL: %%login_url%%\n</p>\n<p>Please feel free to contact us, We will be glad to help.</p>\n<p>%%signature%%</p>'),
(282, 'registration_approval_info', 'Account awaiting approval'),
(283, 'awaiting_approval_subject', 'Your Qualitair account request is being verified.'),
(284, 'awaiting_approval_message', 'Dear %%display_name%%,\n<p>Thank you for creating an account on %%site_name%%.</p>\n<p>Your account is being verified by our quality team. We will get back to you with your account status shortly.</p>\n<p>In the meantime, please feel free to contact us for any queries.</p>\n<p>%%signature%%</p>'),
(285, 'awaiting_approval_subject_admin', 'Qualitair user accounts awaiting your approval.'),
(286, 'awaiting_approval_message_admin', 'Dear Admin,\n<p>There are new accounts awaiting your approvals.</p>\n<p>Please login to <a href="%%site_url%%/wp-admin">%%site_name%% dashboard</a> to approve or decline these accounts.</p>'),
(287, 'invitation_email_heading', 'Invitation Approved Email Messages'),
(288, 'invitation_email_subject', 'Qualitair Invite'),
(289, 'invitation_email_message', '<p>Dear User,</p>\n<p>You have been invited to get a new user account on <a href="%%site_url%%">%%site_name%%</a> by %%display_name%%.</p>\n<p>To start your user registration, please follow this link:<br />\n<a href="%%invitation_link%%">%%invitation_link%%</a></p>\n<p>After you do this, you will be able setup your new %%site_name%% account.</p>\n<p>%%signature%%</p>'),
(290, 'cjfm_customize_emails_password_heading', 'Reset Password Email Messages'),
(291, 'reset_password_info', 'Below email message will be sent to users when they try to reset their password, depending upon the setup type used in the shortcode. <br /> You can use dynamic variables in your messages to personalize the email and populate different values. <a target="_blank" href="http://qlt-ecommerce.com:8080/wp-admin/admin.php?page=cjfm&callback=cjfm_customize_emails_variables">Click here</a> to view dynamic variable list.'),
(292, 'send_password_link_subject', 'Your Qualitair login details'),
(293, 'send_password_link_message', 'Hello %%display_name%%,\n<p>We received a request to reset the password of your %%site_name%% account (%%user_login%%).</p>\n<p>Please follow the link below to reset your password:</p>\n<p>\n%%reset_password_confirmation_link%%\n</p>\n<p>If you have not sent this request, please ignore this email and nothing will change on your account.</p>\n<p>%%signature%%</p>'),
(294, 'dynamic_variables', 'Dynamic Variables (to be used in email messages above)'),
(295, 'dynamic_variables_list', '<code>%%signature%%</code> : Returns email signature<br /><br /><code>%%site_name%%</code> : Returns website name<br /><br /><code>%%site_url%%</code> : Returns website url<br /><br /><code>%%invitation_link%%</code> : Returns invitation link if registration type is set to invitation only.<br /><br /><code>%%login_url%%</code> : Returns login page url specified under page setup<br /><br /><code>%%register_url%%</code> : Returns register page url specified under page setup<br /><br /><code>%%reset_password_url%%</code> : Returns reset password page url specified under page setup<br /><br /><code>%%profile_url%%</code> : Returns profile page url specified under page setup<br /><br /><code>%%ID%%</code> : Returns user database ID<br /><br /><code>%%user_login%%</code> : Returns username<br /><br /><code>%%user_email%%</code> : Returns email address<br /><br /><code>%%user_url%%</code> : Retunrs user url<br /><br /><code>%%user_registered%%</code> : Returns user registration date and time<br /><br /><code>%%display_name%%</code> : Returns user display name (fallback user_login), works only for registered users.<br /><br /><code>%%first_name%%</code> : Returns user first name (fallback display_name), works only for registered users.<br /><br /><code>%%last_name%%</code> : Retruns user last name (fallback blank), works only for registered users.<br /><br /><code>%%description%%</code> : Returns user description, works only for registered users.<br /><br /><code>%%aim%%</code> : Returns user AOL ID, works only for registered users.<br /><br /><code>%%yim%%</code> : Returns user Yahoo ID, works only for registered users.<br /><br /><code>%%jabber%%</code> : Returns user Jabber ID, works only for registered users.<br /><br /><code>%%cjfm_rp%%</code> : Replaced with newly created  user password and removed once password is sent.<br /><br /><code>%%cjfm_last_login%%</code> : Returns user last login date and time<br /><br /><code>%%cjfm_login_ip%%</code> : Returns user last login IP address<br /><br /><code>%%reset_password_confirmation_link%%</code> : Returns reset password confirmation link URL, works only in password reset email message.'),
(303, 'wp_admin_bar', 'enable'),
(304, 'button_color', 'info'),
(305, 'invitations_heading', 'Invitations Setup'),
(306, 'request_invitation_form', 'yes'),
(307, 'invititations_register_form_message', 'You need an invitation to join Qualitair.'),
(308, 'facebook_info', '1. Create Facebook application at <a href="https://developers.facebook.com/apps/" target="_blank">https://developers.facebook.com/apps/</a><br>2. Make sure to specify App Domains<br>3. "Website with Facebook Login" must be checked, but for "Site URL", you can enter any landing URL.<br>4. Copy the App ID & App Secret and specify in the boxes below.'),
(309, 'Twitter_info', '1. Create Twitter application at <a href="https://dev.twitter.com/apps" target="_blank">https://dev.twitter.com/apps</a><br>2. Make sure to enter a Callback URL or callback will be disallowed. You can enter your website Url.<br>3. Register your domains at @Anywhere domains. Twitter only allows authentication from authorized domains.<br>4. Copy the App Key & App Secret and specify in the boxes below.'),
(310, 'Google_info', '1. Create a Google APIs project at <a href="https://code.google.com/apis/console/" target="_blank">https://code.google.com/apis/console/</a><br>2. You do not have to enable any services from the Services tab.<br>3. Make sure to go to <b>API Access</b>  tab and <b>Create an OAuth 2.0 client ID</b>.<br>4. Choose <b>Web application</b>  for <b>Application type</b>.<br>5. Make sure that redirect URI is set to actual OAuth 2.0 callback URL specified above.<br>6. Copy the Client ID & Client Secret and specify in the boxes below.'),
(311, 'LinkedIn_info', '1. Create LinkedIn application at <a href="https://www.linkedin.com/secure/developer" target="_blank">https://www.linkedin.com/secure/developer</a><br>2. Enter your domain at JavaScript API Domain<br>3. There is no need to enter OAuth Redirect URL<br>4. Copy the App Key & App Secret and specify in the boxes below.'),
(312, 'facebook_heading', 'Facebook Setup');
